const mousemoveParallax=document.querySelector(".mousemove-parallax");function parallax(e){this.querySelectorAll(".mousemove-parallax__layer").forEach(a=>{const t=a.getAttribute("data-speed"),l=(window.innerWidth-e.pageX*t)/100,o=(window.innerWidth-e.pageY*t)/100;a.style.transform=`\n          translateX(${l}px)\n          translateY(${o}px)\n          `})}mousemoveParallax.addEventListener("mousemove",parallax);