const deg=6,hr=document.querySelector("#hr"),mn=document.querySelector("#mn"),sc=document.querySelector("#sc");setInterval(()=>{let e=new Date,t=30*e.getHours(),r=6*e.getMinutes(),a=6*e.getSeconds();hr.style.transform=`rotateZ(${t+r/12}deg)`,mn.style.transform=`rotateZ(${r}deg)`,sc.style.transform=`rotateZ(${a}deg)`});const mousemoveParallax=document.querySelector(".mousemove-parallax");function parallax(e){this.querySelectorAll(".mousemove-parallax__layer").forEach(t=>{const r=t.getAttribute("data-speed"),a=(window.innerWidth-e.pageX*r)/100,o=(window.innerWidth-e.pageY*r)/100;t.style.transform=`\n          translateX(${a}px)\n          translateY(${o}px)\n          `})}mousemoveParallax.addEventListener("mousemove",parallax);